// frontend/src/routes/ProtectedRoute.jsx
import React, { useEffect, useState } from 'react';
import { Navigate, useLocation } from 'react-router-dom';
import AuthService from '../services/authService';

const ProtectedRoute = ({ children, adminOnly = false }) => {
  const location = useLocation();
  const [isLoading, setIsLoading] = useState(true);
  const [isAuthorized, setIsAuthorized] = useState(false);

  useEffect(() => {
    // Verificar autenticación
    const checkAuth = () => {
      const isAuthenticated = AuthService.isAuthenticated();
      if (!isAuthenticated) {
        setIsAuthorized(false);
        setIsLoading(false);
        return;
      }

      // Si requiere rol de admin, verificarlo
      if (adminOnly) {
        const currentUser = AuthService.getCurrentUser();
        if (currentUser && currentUser.role === 'admin') {
          setIsAuthorized(true);
        } else {
          setIsAuthorized(false);
        }
      } else {
        // Usuario normal autenticado
        setIsAuthorized(true);
      }
      
      setIsLoading(false);
    };

    checkAuth();
  }, [adminOnly]);

  if (isLoading) {
    // Puedes mostrar un spinner aquí mientras verifica
    return <div>Cargando...</div>;
  }

  if (!isAuthorized) {
    // Redirigir a login si no está autorizado
    return <Navigate to="/login" state={{ from: location }} replace />;
  }

  // Si está autorizado, renderizar los hijos (componente protegido)
  return children;
};

export default ProtectedRoute;
